def multiply(a: int, b: int) -> int:
    import sys
    if a > 0 and b > sys.maxsize // a:
        raise ValueError('Integer overflow')
    if a < 0 and b < sys.maxsize // a:
        raise ValueError('Integer overflow')
    return a * b

def add_positive(a: int, b: int) -> int:
    import sys
    if a > sys.maxsize - b:
        raise ValueError('Integer overflow')
    return a + b
